mapper.select:

 			SELECT CONCAT( W_KIND, W_LOCATION, W_NAME, W_VINTAGE ) AS DATA
 			FROM WINE
		 	WHERE CONCAT( W_KIND, W_LOCATION, W_NAME, W_VINTAGE ) LIKE '%' || #{data} || '%';

SELECT *
 FROM  STORE
WHERE  S_SI = #{ u_si }
 AND   S_GU = #{ u_gu }

SELECT *
 FROM  STORE
WHERE  S_NO = #{ s_no }

SELECT *
 FROM  STORE
WHERE S_ID = #{ s_id }
 AND  S_PW = #{ s_pw }

mapper.update:
UPDATE WINELIST
SET    W_AMOUNT  =  #{w_amount},
       W_PRICE   =  #{w_price}
WHERE  S_NO      =  #{s_no} AND WL_IDX  =  #{wl_idx}
