mapper.select:
SELECT *
FROM TUSER
WHERE U_ID = #{ u_id }
 AND  U_PW = #{ u_pw }

mapper.update:
UPDATE TUSER
SET    U_ADDRESS  =  #{u_address}, U_DETAILADDRESS = #{u_detailAddress}, U_POSTCODE = #{u_postcode}, 
U_EXTRAADDRESS = #{u_extraAddress}, U_PHONE    =  #{u_phone}
WHERE  U_NO  =  #{u_no}

Update TUSER
SET     U_PW  =  #{u_pw}
WHERE   U_NO  =  #{u_no}

mapper.delete:
DELETE FAVORITESTORE
WHERE  u_no = #{u_no}
AND    s_no = #{s_no}
